    <script>
        var block_show = false;
        function scrollMore(){
            console.log('asdas')
            var $target = $('#pagenews');

            if (block_show) {
                return false;
            }

            var wt = $(window).scrollTop();
            // var wh = $(window).height();
            var wh = window.innerHeight;

            var et = $target.offset().top;
            var eh = $target.outerHeight();
            var dh = $(document).height();

            if (wt + wh >= et || wh + wt === dh || eh + et < wh){

                // console.log('wt + wh = ' + (wt + wh) );
                // console.log('>=');
                // console.log('et = ' + et);
                // console.log('------------');

                // console.log('wt + wh = ' + (wt + wh) );
                // console.log('===');
                // console.log('dh = ' + dh);
                // console.log('------------');

                // console.log('eh + et = ' + (eh + et) );
                // console.log('<');
                // console.log('wh = ' + wh);
                // console.log('------------');

                var page = $target.attr('data-page');
                page++;
                block_show = true;
                console.log('asdasd')
                $.ajax({
                    url: '<?php if(isset($lang)){ ?>/<?= $lang?><?php } ?>get/article/' + page + '?id=<?=$data['id']?>&category=<?=$data['category_id']?>',
                    dataType: 'html',
                    success: function(data){
                        console.log(data);
                        $('#pagenewsview .news-list').append(test);
                        block_show = false;
                        // curQuest('added');
                    },
                    error: function(data){
                        console.log(data);
                    }
                });

                $target.attr('data-page', page);
                if (page ==  $target.attr('data-max')) {
                    $target.remove();
                }
            }
        }

        $(window).scroll(function(){
            scrollMore();
        });

        $(document).ready(function(){
            scrollMore();
        });
    </script>
